generated.css:
body {
  font-family: "Rubik", sans-serif;
  background-image: url("789f7117b1996a0c9a952726ae18af4f.jpg");
  background-size: cover;
  background-repeat: no-repeat;
  text-shadow: 2px 2px 2px white, -2px -2px 2px white;
}

header h1 {
  font-family: "Creepster", cursive;
  font-size: 3rem;
}
header a {
  display: inline-block;
  transition: 0.25s;
}
header a:hover {
  transform: translate(-1px, 1px);
}

nav {
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: 1px solid black;
}
nav a {
  text-decoration: none;
}
nav a:visited {
  color: black;
}

#card {
  display: flex;
  flex-wrap: wrap;
}

.card {
  border: 1px solid black;
  width: 200px;
  border-radius: 5px;
  box-shadow: 3px 3px 10px grey;
  padding: 10px;
  margin: 10px;
}

main {
  min-height: 50vh;
}
main .warning {
  color: red;
}
main #delete-button {
  background-color: red;
  border: none;
  padding: 7px 10px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 1rem;
  margin: 4px 2px;
  cursor: pointer;
  border-radius: 5px;
  width: 320px;
}

#main-questions {
  margin-left: 30px;
}

form {
  display: flex;
  flex-direction: column;
}
form button {
  background-color: rgba(173, 66, 244, 0.5);
  border: none;
  padding: 7px 10px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 1rem;
  margin: 4px 2px;
  cursor: pointer;
  border-radius: 5px;
  width: 320px;
}
form input[type=text] {
  border: 1px solid black;
  border-radius: 4px;
  padding: 8px;
  margin-bottom: 15px;
  width: 300px;
}
form .button:hover {
  transform: translate(1px, 1px);
}

footer {
  border-top: 1px solid black;
  margin-top: 15px;
}
footer ul {
  display: flex;
  justify-content: center;
}
footer ul li {
  list-style: none;
  padding-right: 20px;
}
